import React, {useState, useEffect} from 'react';
import axios from 'axios';

function CartDetails(){

    let [quantity,setQuantity] =useState(1);
    let [response, setResponse] = useState('');

    let increase=()=>setQuantity(quantity+1);

    let decrease=()=> {
        if(quantity>1)
            setQuantity(quantity-1);
        else
            return false;
    }

    useEffect(() => {
        axios.get('http://localhost:3333/cart').then(result=>{
        setResponse(result.data);
        console.log(result.data.Product_name);

      })
    },[]);


    return(

    <div className='cartItem'>
        <div className='flex1'>
            <img className='cameracart' src='/Images/camera.jpg' alt='camera'/>
        </div>
        <div className='flex2'>
            <div><b><span className='namecart'>{response.Product_name}</span></b></div>
            <div className="">
                <i class="minus circle icon customicon" onClick={decrease} ></i>
                <input className='num' type='text' value={quantity}/>
                <i class="plus circle icon customicon" onClick={increase}></i>


            </div>
            <div className='stock'>{response.Product_status}</div>
            <div className='delete'>Delete From the cart</div>
        </div>
        <div className='flex3'> <span class='pricecart'> &#x20B9; {response.Product_price} </span>
            </div>
    </div>
    );
}

export default CartDetails;
