import React, {useState} from 'react';

function CartDetails(){

    let [quantity,setQuantity] =useState(1);


    let increase=()=> {
        if(quantity>1)
            setQuantity(quantity+1);
        else
            return false;

    }
    let decrease=()=> setQuantity(quantity-1);


    return(
    <div className='cartItem'>
        <div className='flex1'>
            <img className='cameracart' src='/Images/camera.jpg' alt='camera'/>
        </div>
        <div className='flex2'>
            <div><b><span className='namecart'>Fujifilm Camera</span></b></div>
            <div className="">
                <i class="minus circle icon customicon" onClick={decrease} disabled={quantity<=1? true: false}></i>
                <input className='num' type='text' value={quantity}/>
                <i class="plus circle icon customicon" onClick={increase}></i>


            </div>
            <div className='stock'>In Stock</div>
            <div className='delete'>Delete From the cart</div>
        </div>
        <div className='flex3'> <span class='pricecart'> &#x20B9; 35000 </span>
            </div>
    </div>
    );
}

export default CartDetails;
