import React, {useState, useEffect} from 'react';
import axios from 'axios';
import { useForm } from "react-hook-form";
import ConfirmOrder from './ConfirmOrder';


function Shipping() {
    const {register, handleSubmit }= useForm([]);


    const onSubmit = (data) => {
      //debugger
      console.log(JSON.stringify(data));
      console.log(data);

    };

    return (
        <React.Fragment>
            <h1 className="large text-primary">Shipping Address</h1>
        <form className="form" onSubmit={handleSubmit(onSubmit)}>
          <div className="form-group">
        <label>Full Name</label>
        <input type="text" name="fullname" ref={register} />
        </div>
        <div className="form-group">
        <label>Email Id</label>
        <input type="text" name="email" ref={register} />
        </div>
        <div className="form-group">
        <label>Mobile Number</label>
        <input type="text" name="mobilenumber" ref={register} />
        </div>
        <div className="form-group">
        <label>Flat No</label>
        <input type="text" name="flatnum" ref={register} />
        </div>
        <div className="form-group">
        <label>Address</label>
        <textarea type="text" name="address" ref={register} ></textarea>
        </div>
        <div className="form-group">
        <label>Landmark</label>
        <input type="text" name="landmark" ref={register} />
        </div>
        <div className="form-group">
        <label>Pincode</label>
        <input type="text" name="pincode" ref={register} />
        </div>

        <input type="submit" className="btn btn-primary"/>

        {/* <ul>
    {data.map(dataobj =>(
     <li key={dataobj.fullname}> {dataobj.email} {dataobj.flatnum} {dataobj.address}</li>
    ))}
    </ul> */}
      </form>
      <br/>
       <ConfirmOrder /> 

      </React.Fragment>
    );
}



function CartDetails(){

    let [quantity,setQuantity] =useState(1);
    let [response, setResponse] = useState([]);

    let increase=()=>setQuantity(quantity+1);

    let decrease=()=> {
        if(quantity>1)
            setQuantity(quantity-1);
        else
            return false;
    }

    useEffect(() => {
        axios.get('http://localhost:3333/cart').then(result=>{
        setResponse(result.data); 
      })
    },[]);

return(
<>
   {response.map((resp, index)=>{
       return(<div className='cartItem' key={index}>
                <div className='flex1'>
                    <img className='imgcart' src={resp.Product_image} alt='camera'/>
                </div>
                <div className='flex2'>
                    <div><b><span className='namecart'>{resp.Product_name}</span></b></div>
                    <div className="">
                <i class="minus circle icon customicon" onClick={decrease} ></i>
                <input className='num' type='text' value={quantity}/>
                <i class="plus circle icon customicon" onClick={increase}></i>  
            </div>
            <div className='stock'>{resp.Product_status}</div>
            <div className='delete'>Delete From the cart</div>
        </div>
        <div className='flex3'> <span class='pricecart'> &#x20B9; {resp.Product_price} </span></div>
       </div>
       );
   })
   }
   <button  onClick={Shipping} className='checkout ui button' type='submit'>Checkout</button>
</>
);
}
export default CartDetails;
